	public List<PedidoDTO> findAllPedidos() {
		List<PedidoDTO> pedidos = new ArrayList<PedidoDTO>();
		
		try {
			for (Pedido pedi : PedidoDAO.getInstancia().getAll()) {
				pedidos.add(pedi.toDTO());
			}
		} catch (PedidoException e) {
			e.printStackTrace();
		}
	
		return pedidos;
	}
	
